  let { userId, authTokens } = useContext(AuthContext);
  return (
    <div key={index} className="thread">
      <div className="thread__top">
        <div className="thread__author">
          <a href="#" className="thread__authorInfo">
            <div className="avatar avatar--small">
              <img
                src={`http://localhost:8000${message.message.user.avatar}`}
              />
            </div>
            <span>@{message.message.user.username}</span>
          </a>
          <span className="thread__date">
            {timeSince(message.message.created)}
          </span>
        </div>
