import React, { useContext } from "react";
import { timeSince } from "./createdTime";
import AuthContext from "../context/AuthContext";

const ThreadComment = (message) => {
  let { userId, authTokens } = useContext(AuthContext);
  console.log(message);
  return (
    <div key={message.index} className="thread">
      <div className="thread__top">
        <div className="thread__author">
          <a href="#" className="thread__authorInfo">
            <div className="avatar avatar--small">
              <img
                src={`http://localhost:8000${message.message.user.avatar}`}
              />
            </div>
            <span>@{message.message.user.username}</span>
          </a>
          <span className="thread__date">
            {timeSince(message.message.created)}
          </span>
        </div>
        {message.message.user.id === userId && (
          <div className="thread__delete">
            <svg
              version="1.1"
              xmlns="http://www.w3.org/2000/svg"
              width="32"
              height="32"
              viewBox="0 0 32 32"
            >
              <title>remove</title>
              <path d="M27.314 6.019l-1.333-1.333-9.98 9.981-9.981-9.981-1.333 1.333 9.981 9.981-9.981 9.98 1.333 1.333 9.981-9.98 9.98 9.98 1.333-1.333-9.98-9.98 9.98-9.981z"></path>
            </svg>
          </div>
        )}
      </div>
      <div className="thread__details">{message.message.body}</div>
    </div>
  );
};

export default ThreadComment;
