import React, { useState } from "react";
import { Link } from "react-router-dom";
import InputField from "../components/InputField";

const CreateRoom = () => {
  const [formData, setFormData] = useState({
    roomName: "",
    topic: "",
    aboutRoom: "",
  });

  // Handle input changes for all fields
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();

    // Check if passwords match
    if (formData.pass !== formData.c_pass) {
      alert("Passwords do not match!"); // You can also set some state here to display an error message in the UI.
      return;
    }

    console.log(formData);
    // Proceed with the rest of the form submission logic
  };
  return (
    <main className="create-room layout">
      <div className="container">
        <div className="layout__box">
          <div className="layout__boxHeader">
            <div className="layout__boxTitle">
              <Link to="/">
                <svg
                  version="1.1"
                  xmlns="http://www.w3.org/2000/svg"
                  width="32"
                  height="32"
                  viewBox="0 0 32 32"
                >
                  <title>arrow-left</title>
                  <path d="M13.723 2.286l-13.723 13.714 13.719 13.714 1.616-1.611-10.96-10.96h27.625v-2.286h-27.625l10.965-10.965-1.616-1.607z"></path>
                </svg>
              </Link>
              <h3>Create Study Room</h3>
            </div>
          </div>
          <div className="layout__body scroll">
            <form className="form" onSubmit={handleSubmit}>
              <InputField
                props={{
                  id: "room_name",
                  name: "room_name",
                  type: "text",
                  label: "Room Name",
                  placeholder: "E.g. Mastering Python + Django",
                  required: true,
                  value: formData.roomName,
                  onChange: handleInputChange,
                }}
              />

              <div className="form__group">
                <label for="room_topic">Topic</label>
                <input
                  required
                  type="text"
                  name="topic"
                  id="room_topic"
                  list="topic-list"
                  value={topic}
                  onChange={handleInputChange()}
                />
                <datalist id="topic-list">
                  <select id="room_topic">
                    <option value="">Select your topic</option>
                    <option value="Python">Python</option>
                    <option value="Django">Django</option>
                  </select>
                </datalist>
              </div>

              <div className="form__group">
                <label for="room_about">About</label>
                <textarea
                  name="room_about"
                  id="room_about"
                  placeholder="Write about your study group..."
                  value={aboutRoom}
                  onChange={handleInputChange()}
                ></textarea>
              </div>
              <div className="form__action">
                <a className="btn btn--dark" href="index.html">
                  Cancel
                </a>
                <button className="btn btn--main" type="submit">
                  Create Room
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </main>
  );
};

export default CreateRoom;
