import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  userId: null,
  user: null,
  authTokens: null,
  loading: null,
  userName: null,
  userEmail: null,
  isLoggedIn: null,
};

const userDataSlice = createSlice({
  name: "userData",
  initialState,
  reducers: {
    setUserId: (state, action) => {
      state.userId = action.payload;
      console.log(action.payload); // Debugging: Ensure to eventually remove or conditionally log based on environment.
    },
  },
});

// Export actions and reducer
export const { setUserId } = userDataSlice.actions;
export default userDataSlice.reducer;
